MEGA_MENU.code:
{% comment %}
  Mega Menu - snippets/mega-menu.liquid
{% endcomment %}
{% if link.links.size > 0 %}
  <div class="mega-menu">
    {% for childlink in link.links %}
      <div class="mega-menu__column">
        <h4><a href="{{ childlink.url }}">{{ childlink.title }}</a></h4>
      </div>
    {% endfor %}
  </div>
{% endif %}

COLLECTION_FILTERS.code:
{% comment %}
  Filters - sections/filters.liquid
{% endcomment %}
<div class="filters">
  <h3>Filter by</h3>
  {% assign types = collection.products | map: 'type' | uniq %}
  {% for type in types %}
    <label>
      <input type="checkbox" name="filter" value="{{ type }}">
      {{ type }}
    </label>
  {% endfor %}
</div>

QUICK_SHOP.code:
{% comment %}
  Quick Shop - snippets/quick-shop.liquid
{% endcomment %}
<div class="modal quick-shop" data-quick-shop hidden>
  <button data-modal-close>&times;</button>
  <h3>{{ product.title }}</h3>
  <div class="price">{{ product.price | money }}</div>
  {% form 'product', product %}
    <input type="hidden" name="id" value="{{ product.selected_or_first_available_variant.id }}">
    <button type="submit">ADD TO CART</button>
  {% endform %}
</div>

STICKY_HEADER.code:
{% comment %}
  Header - sections/header.liquid
{% endcomment %}
<header class="header" data-sticky-header>
  <a href="/">{{ shop.name }}</a>
  <nav>
    {% for link in linklists.main-menu.links %}
      <a href="{{ link.url }}">{{ link.title }}</a>
    {% endfor %}
  </nav>
  <button data-cart-open>Cart ({{ cart.item_count }})</button>
</header>

NEWSLETTER.code:
{% comment %}
  Newsletter - snippets/newsletter.liquid
{% endcomment %}
<div class="modal newsletter" data-newsletter hidden>
  <button data-modal-close>&times;</button>
  <h3>Get 10% Off</h3>
  {% form 'customer' %}
    <input type="email" name="contact[email]" placeholder="Your email" required>
    <button type="submit">SUBSCRIBE</button>
  {% endform %}
</div>
